--  DDL for View VADMIN
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "CAMPUS"."VADMIN" ("NOTA", "CALIFICACION", "ASIGNTURA", "AÑO", "DNI", "NOMBRE", "APELLIDOS", "CIUDAD", "PAIS", "EMAIL", "DESCRIPCION", "IMAGEN", "DESC_IMAGEN", "INTERESES") AS 
  select M.NOTA, M.CALIFICACION, ASG.NOMBRE Asigntura, ASG.AÑO, U.DNI, U.NOMBRE, U.APELLIDOS, U.CIUDAD, U.PAIS, U.EMAIL, U.DESCRIPCION, U.IMAGEN, U.DESC_IMAGEN, U.INTERESES
	from (MATRICULA M JOIN ASIGNATURAS ASG ON (M.COD_ASIGNATURA = ASG.COD_ASIGNATURA))
      	JOIN USUARIOS U ON (M.COD_USUARIO = U.COD_USUARIO);
