      dbms_output.put_line(statement);
      EXECUTE IMMEDIATE statement;
      statement := 'GRANT R_ALUMNO TO '|| aux;
      dbms_output.put_line(statement);
      EXECUTE IMMEDIATE statement;  
   END IF;
   END LOOP;
